CREATE TABLE IF NOT EXISTS "notifications" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "message" text NOT NULL, "type" varchar(50) NOT NULL, "triggered_by_id" bigint NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "work_id" bigint NULL REFERENCES "work" ("id") DEFERRABLE INITIALLY DEFERRED);
