CREATE TABLE IF NOT EXISTS "work" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "work_code" varchar(100) NULL, "type" varchar(100) NOT NULL, "words" integer NOT NULL, "comment" text NULL, "deadline" datetime NOT NULL, "status" varchar(100) NOT NULL, "is_submitted" bool NOT NULL, "assigned_by_id" bigint NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "assigned_to_id" bigint NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "author_id" bigint NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "uptaken_by_id" bigint NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "assigned_is_read" bool NOT NULL, "uptaken_is_read" bool NOT NULL);
CREATE TABLE IF NOT EXISTS "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "users" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" datetime NOT NULL);
